  const hook = () =>{
    const eventHandler = response =>{
      setCountries(response.data)
    }

    const promise = axios.get('https://restcountries.eu/rest/v2/all')
    promise.then(eventHandler)
  }

  useEffect(hook, [])

  return(
    <div>
      search: <input />
    </div>
  )
}

